mapper.sql:
FROM SECURITY_PERMISSION SP

	
	  	 AND SP.SP_ID=#{sp_id} 
	
	
	  	 AND SP.SP_NAME=#{sp_name} 
	
	
	  	 AND SP.SP_PARENT_ID=#{sp_parent_id} 
	
	
	  	 AND SP.SYS_ID=#{sys_id} 
	
	    AND SP.SP_VISIBLE='Y'

mapper.select:
SELECT distinct SP.SP_PARENT_ID,SP.SP_ID,SP.SYS_ID,SP.SP_NAME,SP.SP_DESC,SP.HREF,SP.TARGET,SP.SP_VISIBLE,SP.PERMISSION,SP.VERSION,SP.SP_TYPE,SP.ICON,SP.RANK FROM SECURITY_PERMISSION SP LEFT JOIN SECURITY_ROLE_PERMISSION SRP on SP.SP_ID = SRP.SP_ID LEFT JOIN SECURITY_USER_ROLE SUR ON SRP.SR_ID = SUR.SR_ID LEFT JOIN SECURITY_USER SU on SUR.SU_ID = SU.SU_ID WHERE SP.SP_TYPE != 'button'
AND SU.SU_ACCOUNT = #{account}
AND SP.SYS_ID = #{sysId}
AND SRP.SR_ID = #{roleId}
AND SP.SP_VISIBLE='Y' ORDER BY SP.RANK

SELECT SP.SP_PARENT_ID,SP.SP_ID,SP.SYS_ID,SP.SP_NAME,SP.SP_DESC,SP.HREF,SP.TARGET,SP.SP_VISIBLE,SP.PERMISSION,SP.VERSION,SP.SP_TYPE,SP.ICON,SP.RANK FROM SECURITY_PERMISSION SP WHERE SP.SP_TYPE != 'button'
AND SP.SYS_ID = #{sysId}
AND SP.SP_VISIBLE='Y' ORDER BY SP.RANK
